function accordion() {
  document.querySelectorAll(".accordion__item-trigger").forEach((c) => {
    c.addEventListener("click", () => {
      const t = c.parentNode;
      t.classList.contains("accordion__item-active")
        ? t.classList.remove("accordion__item-active")
        : (document
            .querySelectorAll(".accordion__item")
            .forEach((c) => c.classList.remove("accordion__item-active")),
          t.classList.add("accordion__item-active"));
    });
  });
}
accordion();
const btnsRotate = document.querySelectorAll(".rotate");
btnsRotate.forEach((c) => {
  c.addEventListener("click", () => {
    c.classList.toggle("animation");
  });
});

const smoothLinks = document.querySelectorAll('a[href^="#"]');
for (let smoothLink of smoothLinks) {
  smoothLink.addEventListener("click", function (e) {
    e.preventDefault();
    const id = smoothLink.getAttribute("href");

    document.querySelector(id).scrollIntoView({
      behavior: "smooth",
      block: "start",
    });
  });
}
